/**
   * @param {Input} input Value comes from the "Variables" field 
   * @param {Array.<FileInput>} fileInput Value comes from the "Files" field 
   */
  exports.step = function(input, fileInput) {
      var items = input.items;

      if (typeof items === 'string') {
          items = JSON.parse(items);
      }

      items = items || [];

      var filtered = items.filter(function(item) {
          var score = parseFloat(item.properties.relevance_score);
          return !isNaN(score) && score > 0.5;
      });

      filtered.sort(function(a, b) {
          return parseFloat(b.properties.relevance_score) - parseFloat(a.properties.relevance_score);
      });

      return { filtered_items: filtered };
  };

/** 
 * @param {Object} input - Contains the array and filter date
 * @param {Array.<Object>} input.items - Array of objects to filter
 * @param {string} input.dateField - Name of the date field to filter on
 * @param {string|Date} input.filterDate - Filter items greater than this date
 * @param {Array.<FileInput>} fileInput - Value comes from the "Files" field 
 */
exports.step = function(input, fileInput) {
    const { items, dateField, filterDate } = input;
    const threshold = new Date(filterDate);

    return items.filter(item => new Date(item.properties[dateField]) > threshold);
};

/** 
  * @param {Input} input Value comes from the "Variables" field 
  * @param {Array.<FileInput>} fileInput Value comes from the "Files" field 
  */
 exports.step = function(input, fileInput) {
     let sanitized = input.markdown;

     // Escape backslashes first (order matters)
     sanitized = sanitized.replace(/\\/g, '\\\\');
     // Escape double quotes
     sanitized = sanitized.replace(/"/g, '\\"');
     // Escape newlines
     sanitized = sanitized.replace(/\n/g, '\\n');
     // Escape carriage returns
     sanitized = sanitized.replace(/\r/g, '\\r');
     // Escape tabs
     sanitized = sanitized.replace(/\t/g, '\\t');

     return { markdown: sanitized };
 };

